import './App.css';
import io from "socket.io-client";
import "react-chat-elements/dist/main.css";
import { MessageBox, ChatItem} from "react-chat-elements";

import {useState, useEffect, useRef} from 'react';

const App = () => {
  const socket = useRef(null);
  const [display, setDisplay] = useState([]);
  const [text, setText] = useState("");

  const addMessage = (message) => {
    let cur = display.slice();
    cur.reverse();
    cur.push(<p className = "font">{message}</p>)
    cur.reverse();
    setDisplay(cur);
  }

  console.log(display);

  useEffect(() => {
    socket.current = io.connect("http://localhost:3006/");
    return () => {
      socket.current.disconnect(); 
    }
  }, []);

  useEffect(() => {
    socket.current.on('receiveMessage', (message) => {
      console.log(message);
      addMessage(message);
    });
    return () => {
      socket.current.off('receiveMessage');
    }
  }, [display]);

  return (
    <div className = "container">
      <div className="App">
        {display}
        <div className = "bar">
          <textarea value = {text} type = "input" className = "nohover" onChange = {(e) => {setText(e.target.value)}}/>
          <button className = "btn" onClick = {() => {
            setText("");
            socket.current.emit("")
          }} />
        </div>
      </div>
    </div>
  );
}

export default App;
